import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
} from "react-router-dom";
import { motion } from "framer-motion";
import { ArrowRight } from "lucide-react";
import "./App.css";

import saveIcon from "./assets/icons/save-icon.png";
import exportIcon from "./assets/icons/export-icon.png";
import logo from "./assets/icons/logo.png";

const avatar = "https://i.pravatar.cc/150?img=10";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  if (!isAuthenticated) {
    return (
      <LoginPage
        setIsAuthenticated={setIsAuthenticated}
        username={username}
        setUsername={setUsername}
        password={password}
        setPassword={setPassword}
      />
    );
  }

  return (
    <Router>
      <Routes>
        <Route path="/" element={<MainPage />} />
        <Route path="/dashboard" element={<ComingSoon />} />
        <Route path="/historico" element={<ComingSoon />} />
        <Route path="/configuracoes" element={<ComingSoon />} />
      </Routes>
    </Router>
  );
}

function LoginPage({
  setIsAuthenticated,
  username,
  setUsername,
  password,
  setPassword,
}) {
  const [loginError, setLoginError] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    if (username === "admin" && password === "1234") {
      setIsAuthenticated(true);
      setLoginError("");
    } else {
      setLoginError("Usuário ou senha inválidos!");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-6">
      <form
        onSubmit={handleLogin}
        className="bg-white p-8 rounded-lg shadow-md w-full max-w-sm space-y-4"
      >
        <img
          src={logo}
          alt="Logo"
          className="mx-auto mb-4 w-48 h-48 object-contain"
        />
        <h2 className="text-2xl font-bold text-center">Login</h2>

        {loginError && (
          <p className="text-red-500 text-sm text-center">{loginError}</p>
        )}

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Usuário
          </label>
          <input
            type="text"
            className="w-full px-3 py-2 border rounded-lg text-sm"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Digite o usuário"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Senha
          </label>
          <input
            type="password"
            className="w-full px-3 py-2 border rounded-lg text-sm"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Digite a senha"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition-all"
        >
          Entrar
        </button>
      </form>
    </div>
  );
}

function MainPage() {
  const [categoria, setCategoria] = useState("");
  const [material, setMaterial] = useState("");
  const [tom, setTom] = useState("");
  const [beneficios, setBeneficios] = useState("");
  const [titulo, setTitulo] = useState("");
  const [descricao, setDescricao] = useState("");
  const [gerando, setGerando] = useState(false);
  const [historico, setHistorico] = useState([]);
  const [itemSelecionado, setItemSelecionado] = useState(null);

  const categorias = ["Camisa", "Calçado", "Bolsa", "Acessório"];
  const tons = ["Informal", "Profissional", "Divertido", "Elegante"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setGerando(true);

    const dados = { categoria, beneficios, material };

    try {
      const resposta = await fetch("http://localhost:8000/gerar", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(dados),
      });

      const json = await resposta.json();
      const texto = json.resultado;

      const match = texto.match(
        /t[ií]tulo[:\-\s]*([^\n]*)[\n\r]+descri[cç][aã]o[:\-\s]*([\s\S]*)/i
      );
      if (match) {
        const novoItem = {
          titulo: match[1].trim(),
          descricao: match[2].trim(),
        };
        setItemSelecionado(novoItem);
        setHistorico([novoItem, ...historico]);
      } else {
        const novoItem = { titulo: "Gerado com IA", descricao: texto };
        setItemSelecionado(novoItem);
        setHistorico([novoItem, ...historico]);
      }
    } catch (error) {
      const novoItem = { titulo: "Erro", descricao: "Erro ao gerar conteúdo." };
      setItemSelecionado(novoItem);
      setHistorico([novoItem, ...historico]);
    }

    setGerando(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6 font-sans">
      <header className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-semibold text-gray-900">
          Gerador de Títulos e Descrições
        </h1>
        <div className="flex items-center gap-6 text-sm text-gray-600">
          <div className="hidden md:flex gap-6">
            <Link to="/dashboard" className="hover:text-blue-600 text-lg">
              Dashboard
            </Link>
            <Link to="/historico" className="hover:text-blue-600 text-lg">
              Histórico
            </Link>
            <Link to="/configuracoes" className="hover:text-blue-600 text-lg">
              Configurações
            </Link>
          </div>
          <img
            src={avatar}
            alt="Avatar"
            className="w-12 h-12 rounded-full object-cover border-2 border-gray-400"
          />
        </div>
      </header>

      <hr className="mb-6" />

      {/* Layout Principal */}
      <div className="flex flex-col md:flex-row gap-6">
        {/* Formulário */}
        <div className="w-full md:w-1/3 bg-white p-4 rounded-lg shadow-md">
          <form onSubmit={handleSubmit} className="space-y-3">
            <label className="block text-xs font-medium text-gray-700">
              Categoria
            </label>
            <select
              className="w-full px-3 py-1.5 rounded border text-sm"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            >
              <option value="">Selecione a categoria</option>
              {categorias.map((cat) => (
                <option key={cat}>{cat}</option>
              ))}
            </select>

            <label className="block text-xs font-medium text-gray-700">
              Material
            </label>
            <input
              className="w-full px-3 py-1.5 rounded border text-sm"
              placeholder="Material (opcional)"
              value={material}
              onChange={(e) => setMaterial(e.target.value)}
            />

            <label className="block text-xs font-medium text-gray-700">
              Tom Desejado
            </label>
            <select
              className="w-full px-3 py-1.5 rounded border text-sm"
              value={tom}
              onChange={(e) => setTom(e.target.value)}
            >
              <option value="">Selecione o tom</option>
              {tons.map((t) => (
                <option key={t}>{t}</option>
              ))}
            </select>

            <label className="block text-xs font-medium text-gray-700">
              Benefícios
            </label>
            <textarea
              className="w-full px-3 py-1.5 rounded border text-sm"
              placeholder="Benefícios"
              value={beneficios}
              onChange={(e) => setBeneficios(e.target.value)}
              rows={2}
            ></textarea>

            <button
              type="submit"
              disabled={gerando}
              className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700"
            >
              {gerando ? "Gerando..." : "Gerar com IA"}
            </button>
          </form>
        </div>

        {/* Resultado no meio */}
        <div className="w-full md:w-2/3 bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-center text-center">
          {itemSelecionado ? (
            <>
              <h2 className="text-2xl font-bold text-gray-800 mb-4">
                {itemSelecionado.titulo}
              </h2>
              <p className="text-gray-600">{itemSelecionado.descricao}</p>
            </>
          ) : (
            <p className="text-gray-400">
              Gere um conteúdo ou selecione no histórico
            </p>
          )}
        </div>
      </div>

      {/* Histórico */}
      <div className="mt-8 bg-white p-4 rounded-lg shadow-md">
        <h3 className="text-sm font-semibold mb-2">Histórico</h3>
        <ul className="space-y-2">
          {historico.map((item, idx) => (
            <motion.li
              key={idx}
              whileHover={{ scale: 1.01 }}
              className="cursor-pointer px-3 py-2 rounded-lg border hover:bg-gray-50"
              onClick={() => setItemSelecionado(item)}
            >
              <div className="flex justify-between items-center">
                <div>
                  <p className="font-medium truncate text-sm">{item.titulo}</p>
                  <p className="text-xs text-gray-500">Gerado por IA</p>
                </div>
                <ArrowRight className="w-4 h-4 text-gray-500" />
              </div>
            </motion.li>
          ))}
        </ul>
      </div>
    </div>
  );
}

