import React, { useState } from "react";
import { FiEdit2 } from "react-icons/fi";
import { motion } from "framer-motion";
import { ArrowRight } from "lucide-react";
const avatar = "https://i.pravatar.cc/56";
import "./App.css";

function App() {
  const [categoria, setCategoria] = useState("");
  const [material, setMaterial] = useState("");
  const [tom, setTom] = useState("");
  const [beneficios, setBeneficios] = useState("");
  const [titulo, setTitulo] = useState("");
  const [descricao, setDescricao] = useState("");
  const [gerando, setGerando] = useState(false);
  const [historico, setHistorico] = useState([]);

  const categorias = ["Camisa", "Calçado", "Bolsa", "Acessório"];
  const tons = ["Informal", "Profissional", "Divertido", "Elegante"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setGerando(true);
    setTitulo("");
    setDescricao("");

    const dados = { categoria, beneficios, material };

    try {
      const resposta = await fetch("http://localhost:8000/gerar", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(dados),
      });

      const json = await resposta.json();
      const texto = json.resultado;

      const match = texto.match(/t[ií]tulo[:\-\s]*([^\n]*)[\n\r]+descri[cç][aã]o[:\-\s]*([\s\S]*)/i);
      if (match) {
        setTitulo(match[1].trim());
        setDescricao(match[2].trim());
      } else {
        setTitulo("Gerado com IA");
        setDescricao(texto);
      }
    } catch (error) {
      setTitulo("Erro");
      setDescricao("Erro ao gerar conteúdo.");
    }

    setGerando(false);
  };

  const salvar = () => {
    if (!titulo || !descricao) return;
    const novo = { categoria, material, tom, beneficios, titulo, descricao };
    const historicoAtualizado = [novo, ...historico];
    if (historicoAtualizado.length > 10) historicoAtualizado.pop();
    setHistorico(historicoAtualizado);
  };

  const exportar = () => {
    const blob = new Blob([
      `Título: ${titulo}\n\nDescrição: ${descricao}`
    ], { type: "text/plain;charset=utf-8" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${titulo.replace(/\s+/g, "_")}.txt`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const carregarHistorico = (item) => {
    setCategoria(item.categoria);
    setMaterial(item.material);
    setTom(item.tom);
    setBeneficios(item.beneficios);
    setTitulo(item.titulo);
    setDescricao(item.descricao);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6 font-sans">
      <header className="flex justify-between items-center mb-4">
        <h1 className="text-xl font-semibold text-gray-900">
          Gerador de Títulos e Descrições
        </h1>
        <div className="flex items-center gap-6 text-sm text-gray-600">
          <a href="#" className="hover:text-blue-600">Dashboard</a>
          <a href="#" className="hover:text-blue-600">Histórico</a>
          <a href="#" className="hover:text-blue-600">Configurações</a>
          <img src={avatar} alt="Avatar" className="w-9 h-9 rounded-full object-cover border" />
        </div>
      </header>

      <hr className="mb-4" />

      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-1">
          Gerador de Títulos e Descrições com IA
        </h2>
        <p className="text-sm text-gray-500">
          Crie títulos e descrições de produtos atraentes sem esforço com IA.
        </p>
      </div>

      <div className="flex justify-between gap-8">
        {/* Formulário + Resultados */}
        <div className="w-full max-w-xl space-y-4">
          <form onSubmit={handleSubmit} className="space-y-3">
            <label className="block text-xs font-medium text-gray-700">Categoria do Produto</label>
            <select
              className="w-full px-3 py-1.5 rounded border text-sm"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            >
              <option value="">Selecione a categoria</option>
              {categorias.map((cat) => (
                <option key={cat}>{cat}</option>
              ))}
            </select>

            <div className="flex gap-3">
              <div className="w-1/2">
                <label className="block text-xs font-medium text-gray-700">Material</label>
                <input
                  className="w-full px-3 py-1.5 rounded border text-sm"
                  placeholder="Insira o material (opcional)"
                  value={material}
                  onChange={(e) => setMaterial(e.target.value)}
                />
              </div>
              <div className="w-1/2">
                <label className="block text-xs font-medium text-gray-700">Tom Desejado</label>
                <select
                  className="w-full px-3 py-1.5 rounded border text-sm"
                  value={tom}
                  onChange={(e) => setTom(e.target.value)}
                >
                  <option value="">Selecione o tom</option>
                  {tons.map((t) => (
                    <option key={t}>{t}</option>
                  ))}
                </select>
              </div>
            </div>

            <label className="block text-xs font-medium text-gray-700">Benefícios</label>
            <textarea
              className="w-full px-3 py-1.5 rounded border text-sm"
              placeholder="Benefícios"
              value={beneficios}
              onChange={(e) => setBeneficios(e.target.value)}
              rows={3}
            ></textarea>

            <div className="flex justify-center">
              <button
                type="submit"
                disabled={gerando}
                className="bg-blue-600 text-white px-6 py-2 rounded-full hover:bg-blue-700 text-sm font-medium shadow-md"
              >
                {gerando ? "Gerando..." : "Gerar com IA"}
              </button>
            </div>
          </form>

          <div>
            <h2 className="font-medium text-sm mb-2">Resultados</h2>
            <div className="flex items-center mb-2">
              <input
                className="flex-1 px-3 py-1.5 border rounded text-sm"
                value={titulo}
                readOnly
              />
              <FiEdit2 className="ml-2 text-gray-400" />
            </div>
            <textarea
              className="w-full px-3 py-1.5 border rounded text-sm"
              value={descricao}
              readOnly
              rows={3}
            ></textarea>

            <div className="flex gap-4 mt-3">
            <button
  className="flex items-center justify-center p-2 rounded-full bg-green-600 text-white hover:bg-green-700"
  onClick={salvar}
>
  <img src="/path/to/your/save-icon.png" alt="Salvar" className="w-5 h-5 mr-2" />
  Salvar
</button>

<button
  className="flex items-center justify-center p-2 rounded-full bg-blue-600 text-white hover:bg-blue-700"
  onClick={exportar}
>
  <img src="/path/to/your/export-icon.png" alt="Exportar" className="w-5 h-5 mr-2" />
  Exportar
</button>

            </div>
          </div>
        </div>

        {/* Histórico ao lado direito */}
        <div className="w-96">
          <div className="bg-white p-4 rounded-lg shadow max-h-[450px] overflow-y-auto">
            <h3 className="text-sm font-semibold mb-2">Histórico</h3>
            <ul className="space-y-2">
              {historico.map((item, idx) => (
                <motion.li
                  key={idx}
                  whileHover={{ scale: 1.01 }}
                  className="cursor-pointer px-3 py-2 rounded-lg border hover:bg-gray-50"
                  onClick={() => carregarHistorico(item)}
                >
                  <div className="flex justify-between items-center">
                    <div>
                      <p className="font-medium truncate text-sm">{item.titulo}</p>
                      <p className="text-xs text-gray-500">Gerado por IA</p>
                    </div>
                    <ArrowRight className="w-4 h-4 text-gray-500" />
                  </div>
                </motion.li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
