function MainPage() {
  const [categoria, setCategoria] = useState("");
  const [material, setMaterial] = useState("");
  const [tom, setTom] = useState("");
  const [beneficios, setBeneficios] = useState("");
  const [titulo, setTitulo] = useState("");
  const [descricao, setDescricao] = useState("");
  const [gerando, setGerando] = useState(false);
  const [historico, setHistorico] = useState([]);
  const [itemSelecionado, setItemSelecionado] = useState(null);

  const categorias = ["Camisa", "Cal√ßado", "Bolsa", "Acess√≥rio"];
  const tons = ["Informal", "Profissional", "Divertido", "Elegante"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setGerando(true);
    setTitulo("");
    setDescricao("");

    const dados = { categoria, beneficios, material };

    try {
      const resposta = await fetch("http://localhost:8000/gerar", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(dados),
      });

      const json = await resposta.json();
      const texto = json.resultado;

      const match = texto.match(
        /t[i√≠]tulo[:\-\s]*([^\n]*)[\n\r]+descri[c√ß][a√£]o[:\-\s]*([\s\S]*)/i
      );
      if (match) {
        setTitulo(match[1].trim());
        setDescricao(match[2].trim());
        setItemSelecionado({
          titulo: match[1].trim(),
          descricao: match[2].trim(),
        });
      } else {
        setTitulo("Gerado com IA");
        setDescricao(texto);
        setItemSelecionado({ titulo: "Gerado com IA", descricao: texto });
      }
    } catch (error) {
      setTitulo("Erro");
      setDescricao("Erro ao gerar conte√∫do.");
      setItemSelecionado({
        titulo: "Erro",
        descricao: "Erro ao gerar conte√∫do.",
      });
    }

    setGerando(false);
  };

  const salvar = () => {
    if (!titulo || !descricao) return;
    const novo = { categoria, material, tom, beneficios, titulo, descricao };
    const historicoAtualizado = [novo, ...historico];
    if (historicoAtualizado.length > 10) historicoAtualizado.pop();
    setHistorico(historicoAtualizado);
  };

  const exportar = () => {
    const blob = new Blob([`T√≠tulo: ${titulo}\n\nDescri√ß√£o: ${descricao}`], {
      type: "text/plain;charset=utf-8",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${titulo.replace(/\s+/g, "_")}.txt`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6 font-sans">
      <header className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-semibold text-gray-900">
          Gerador de T√≠tulos e Descri√ß√µes
        </h1>
        <div className="flex items-center gap-6 text-sm text-gray-600">
          <div className="hidden md:flex gap-6">
            <Link to="/dashboard" className="hover:text-blue-600 text-lg">
              Dashboard
            </Link>
            <Link to="/historico" className="hover:text-blue-600 text-lg">
              Hist√≥rico
            </Link>
            <Link to="/configuracoes" className="hover:text-blue-600 text-lg">
              Configura√ß√µes
            </Link>
          </div>
          <img
            src={avatar}
            alt="Avatar"
            className="w-12 h-12 rounded-full object-cover border-2 border-gray-400"
          />
        </div>
      </header>

      <hr className="mb-4" />

      {/* Formul√°rio */}
      <div className="w-full max-w-4xl mx-auto space-y-4">
        <form
          onSubmit={handleSubmit}
          className="bg-white p-4 rounded-lg shadow-md"
        >
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-xs font-medium text-gray-700">
                Categoria do Produto
              </label>
              <select
                className="w-full px-3 py-1.5 rounded border text-sm"
                value={categoria}
                onChange={(e) => setCategoria(e.target.value)}
              >
