import React, { useState } from "react";

function App() {
  const [categoria, setCategoria] = useState("");
  const [material, setMaterial] = useState("");
  const [tom, setTom] = useState("");
  const [beneficios, setBeneficios] = useState("");
  const [titulo, setTitulo] = useState("");
  const [descricao, setDescricao] = useState("");
  const [gerando, setGerando] = useState(false);
  const [historico, setHistorico] = useState([]);

  const categorias = ["Camisa", "Calçado", "Bolsa", "Acessório"];
  const tons = ["Informal", "Profissional", "Divertido", "Elegante"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setGerando(true);
    setTitulo("");
    setDescricao("");

    const dados = { categoria, beneficios, material };

    try {
      const resposta = await fetch("http://localhost:8000/gerar", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(dados),
      });

      const json = await resposta.json();
      const texto = json.resultado;

      const match = texto.match(/t[ií]tulo[:\-]?\s*(.*)\n*descri[cç][aã]o[:\-]?\s*(.*)/i);
      if (match) {
        setTitulo(match[1].trim());
        setDescricao(match[2].trim());
      } else {
        setTitulo("Gerado com IA");
        setDescricao(texto);
      }
    } catch (error) {
      setTitulo("Erro");
      setDescricao("Erro ao gerar conteúdo.");
    }

    setGerando(false);
  };

  const salvar = () => {
    if (!titulo || !descricao) return;
    const novo = { categoria, material, tom, beneficios, titulo, descricao };
    setHistorico([novo, ...historico]);
  };

  const carregarHistorico = (item) => {
    setCategoria(item.categoria);
    setMaterial(item.material);
    setTom(item.tom);
    setBeneficios(item.beneficios);
    setTitulo(item.titulo);
    setDescricao(item.descricao);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <header className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-indigo-600">🧠 Gerador de Produtos</h1>
        <div className="flex gap-4 text-sm text-gray-600">
          <a href="#" className="hover:underline">Dashboard</a>
          <a href="#" className="hover:underline">Histórico</a>
          <a href="#" className="hover:underline">Configurações</a>
        </div>
      </header>

      <main className="grid grid-cols-1 md:grid-cols-4 gap-8">
        <div className="md:col-span-3 bg-white shadow rounded-lg p-6">
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block font-medium">Categoria</label>
              <select value={categoria} onChange={(e) => setCategoria(e.target.value)} className="mt-1 w-full border rounded p-2">
                <option value="">Selecione a categoria</option>
                {categorias.map((cat) => <option key={cat}>{cat}</option>)}
              </select>
            </div>

            <div className="flex gap-4">
              <div className="flex-1">
                <label className="block font-medium">Material</label>
                <input value={material} onChange={(e) => setMaterial(e.target.value)} className="mt-1 w-full border rounded p-2" />
              </div>
              <div className="flex-1">
                <label className="block font-medium">Tom</label>
                <select value={tom} onChange={(e) => setTom(e.target.value)} className="mt-1 w-full border rounded p-2">
                  <option value="">Selecione o tom</option>
                  {tons.map((t) => <option key={t}>{t}</option>)}
                </select>
              </div>
            </div>

            <div>
              <label className="block font-medium">Benefícios</label>
              <textarea value={beneficios} onChange={(e) => setBeneficios(e.target.value)} className="mt-1 w-full border rounded p-2" rows={3}></textarea>
            </div>

            <button type="submit" disabled={gerando} className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700">
              {gerando ? "Gerando..." : "Gerar com IA"}
            </button>
          </form>

          <div className="mt-8">
            <h2 className="text-lg font-semibold mb-2">Resultado</h2>
            <input value={titulo} readOnly className="w-full border rounded p-2 mb-2 font-semibold" />
            <textarea value={descricao} readOnly className="w-full border rounded p-2 mb-4" rows={4}></textarea>
            <div className="flex gap-4">
              <button onClick={salvar} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">💾 Salvar</button>
              <button className="bg-gray-300 text-gray-800 px-4 py-2 rounded hover:bg-gray-400">↗ Exportar</button>
            </div>
          </div>
        </div>

        <aside className="bg-white shadow rounded-lg p-4">
          <h3 className="text-md font-bold mb-2">Histórico</h3>
          <ul className="space-y-1 text-sm">
            {historico.map((item, idx) => (
              <li key={idx} onClick={() => carregarHistorico(item)} className="cursor-pointer hover:underline text-indigo-600">
                {item.titulo} <span className="text-gray-400">→</span>
              </li>
            ))}
          </ul>
        </aside>
      </main>
    </div>
  );
}

export default App;
